import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { AlertRow } from "@/components/AlertRow";
import { Input } from "@/components/ui/input";
import { Button } from "@/components/ui/button";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { Search, Filter } from "lucide-react";
import { useState } from "react";

//todo: remove mock functionality
const mockAlerts = [
  {
    id: "1",
    timestamp: "07/11 14:23",
    system: "Core DB Server",
    severity: "critical" as const,
    message: "CPU usage above 95% for 10 minutes",
    details: "[2025-11-07 14:23:15] ERROR: High CPU utilization detected\nProcess: postgres (PID 1234)\nCPU: 96.8%",
  },
  {
    id: "2",
    timestamp: "07/11 14:18",
    system: "API Gateway",
    severity: "down" as const,
    message: "Service unavailable - connection timeout",
    details: "[2025-11-07 14:18:32] CRITICAL: Gateway unreachable",
  },
  {
    id: "3",
    timestamp: "07/11 14:15",
    system: "Web Application",
    severity: "major" as const,
    message: "Response time degradation detected",
  },
  {
    id: "4",
    timestamp: "07/11 14:05",
    system: "Dev Environment",
    severity: "minor" as const,
    message: "Disk usage at 85%",
  },
  {
    id: "5",
    timestamp: "07/11 13:50",
    system: "Backup Server",
    severity: "clear" as const,
    message: "Backup completed successfully",
  },
];

export default function Alerts() {
  const [acknowledged, setAcknowledged] = useState<Record<string, boolean>>({});
  const [searchTerm, setSearchTerm] = useState("");
  const [severityFilter, setSeverityFilter] = useState("all");

  const handleAck = (id: string) => {
    setAcknowledged((prev) => ({ ...prev, [id]: true }));
    console.log(`Alert ${id} acknowledged`);
  };

  return (
    <div className="space-y-6">
      <div>
        <h1 className="text-2xl font-semibold">Quản lý Cảnh báo</h1>
        <p className="text-sm text-muted-foreground">
          Xem và xử lý các cảnh báo từ hệ thống
        </p>
      </div>

      <Card>
        <CardHeader>
          <div className="flex flex-col md:flex-row gap-4">
            <div className="flex-1 relative">
              <Search className="absolute left-3 top-1/2 -translate-y-1/2 h-4 w-4 text-muted-foreground" />
              <Input
                placeholder="Tìm kiếm cảnh báo..."
                className="pl-9"
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                data-testid="input-search-alerts"
              />
            </div>
            <div className="flex gap-2">
              <Select value={severityFilter} onValueChange={setSeverityFilter}>
                <SelectTrigger className="w-40" data-testid="select-filter-severity">
                  <Filter className="h-4 w-4 mr-2" />
                  <SelectValue />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="all">Tất cả</SelectItem>
                  <SelectItem value="down">Down</SelectItem>
                  <SelectItem value="critical">Critical</SelectItem>
                  <SelectItem value="major">Major</SelectItem>
                  <SelectItem value="minor">Minor</SelectItem>
                  <SelectItem value="clear">Clear</SelectItem>
                </SelectContent>
              </Select>
              <Button variant="outline" data-testid="button-clear-filters">
                Xóa bộ lọc
              </Button>
            </div>
          </div>
        </CardHeader>
        <CardContent className="p-0">
          <div className="divide-y">
            {mockAlerts.map((alert) => (
              <AlertRow
                key={alert.id}
                {...alert}
                acknowledged={acknowledged[alert.id]}
                onAcknowledge={handleAck}
              />
            ))}
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

