import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { AlertRuleForm } from "@/components/AlertRuleForm";
import { Plus, Settings } from "lucide-react";
import { StatusBadge } from "@/components/StatusBadge";
import { useState } from "react";
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
} from "@/components/ui/dialog";

//todo: remove mock functionality
const mockRules = [
  {
    id: "1",
    name: "CPU High Usage Alert",
    condition: "cpu_usage > 90 AND duration > 300",
    severity: "critical" as const,
  },
  {
    id: "2",
    name: "Disk Space Warning",
    condition: "disk_usage > 85",
    severity: "major" as const,
  },
  {
    id: "3",
    name: "Service Down Detection",
    condition: "service_status = 'down'",
    severity: "down" as const,
  },
  {
    id: "4",
    name: "Memory Usage Monitor",
    condition: "memory_usage > 80",
    severity: "minor" as const,
  },
];

export default function ConfigRules() {
  const [open, setOpen] = useState(false);

  const handleSubmit = (data: any) => {
    console.log("Rule submitted:", data);
    setOpen(false);
  };

  return (
    <div className="space-y-6">
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-2xl font-semibold">Quản lý Alert Rules</h1>
          <p className="text-sm text-muted-foreground">
            Cấu hình quy tắc phát hiện và cảnh báo sự cố
          </p>
        </div>
